import axios from 'axios';

const cart_url = "http://localhost:9090/cart/";
class CartService {
    async getCart(cartId) {
        return axios.get(cart_url + `getcart/${cartId}`);
    }
    addToCart(username, productId) {
        axios.post(`http://localhost:9090/cart/additem/${username}/${productId}`)
    }

    updateCart(username, productId, quantity) {
        axios.put(`http://localhost:9090/cart/updateitem/${username}/${productId}/${quantity}`)
    }

    deleteCartItem(username, productId) {
        axios.post(`http://localhost:9090/cart/deleteitem/${username}/${productId}`,
        {
           headers: {
            "Access-Control-Allow-Origin" : "*",
            "Access-Control-Allow-Methods":"GET,PUT,POST,DELETE",
           } 
        })
    }

    deleteCart(username) {
        axios.delete(`http://localhost:9090/cart/deletecart/${username}`)
    }
}

export default new CartService();
